sys_script_include.script:
var GetEmailAddress = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define the getEmail function.
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record

	getEmail: function() {
		var userRecord = new GlideRecord("sys_user");          // Create a GlideRecord for the User table.
		userRecord.get(this.getParameter('sysparm_userID'));   // retrieve a record from the User table

		// wpisy do logów ServiceNow SystemLog -> All
		//gs.info('!!! Info: odczytany przez serwer "sysparm_userID": ' + this.getParameter('sysparm_userID'));
		//gs.info('!!! Info: wysłany z serwera adres email: ' + userRecord.email);


		// wpisy na ekranie na formularzu
		//gs.addInfoMessage('odczytany przez serwer "sysparm_userID": ' + this.getParameter('sysparm_userID') );
		//gs.addInfoMessage('wysłany z serwera adres email: ' + userRecord.email);


		return userRecord.email + '';           // Return the email address for the requested rec.
	},
	type: 'GetEmailAddress'
});
